INSERT INTO `lab` (`lab_id`, `lab_name`, `lab_desc`, `lab_index`) VALUES
(1, 'Acid serum test ', '<h2>NOTES</h2><ul><li>Reaction dependent on serum magnesium concentration</li></ul><h3>Indication:</h3><ul><li>To test paroxysmal nocturnal haemoglobinuria (PNH) caused by abnormality of the red cell membrane</li></ul><h2>SAMPLE REQUIREMENT</h2><h2>NORMAL VALUE</h2><h2>METHOD</h2><ul><li>Haemolytic action of complement in acidified serum</li></ul><h3>Positive in:</h3><ul><li>Paroxysmal nocturnal hemoglobinuria (PNH)</li></ul><h3>False positive in:</h3><ul><li>Hereditary or acquired spherocytosis</li><li>Recent transfusion with aged RBC</li><li>Aplastic anemia</li><li>Myeloproliferative syndromes</li><li>Leukemia</li><li>Hereditary dyserythropoietic anemia type II (HEMPAS)</li></ul><h3>Synonyms:</h3><ul><li>Ham test</li><li>Ham''s acid lysis test</li></ul>', 'A'),
(2, 'ACTH stimulation test ', '<h2>NOTES</h2><h3>Indications:</h3><ul><li>To differentiate  adrenal hyperplasia and adrenal adenoma from adrenal carcinoma and Addison''s disease</li><li>To diagnose adrenal insufficiency</li></ul><h2>SAMPLE REQUIREMENT</h2><h2>COMMON FINDINGS</h2><h2>NORMAL VALUE</h2><h3>Three criteria :</h3><ul><li>Basal cortisol - at least 5 micro g/dL</li><li>Incremental increase after Cortrosyn injection - at least 7 micro g/dL</li><li>Final serum cortisol - at least 16 micro g/dL at 30 minutes, 18 micro g/dL at 60 minutes or cortisol increase of more than 10 microg/dL</li><li>Aldosterone increase - more than 5 ng/dL over baseline</li></ul><h2>METHOD</h2><ul><li>Estimate basal plasma cortisol level, urinary 17 ketosteroids (17 KS) and ketogenic steroids (KGS) over 24 hours</li><li>Give 25 units of ACTH diluted in 100 mL of N saline IV lasting for over 8 hours (or cosyntropin is given 0.25 mg 1M or IV )</li><li>Collect serum samples after 30 and 60 minutes</li><li>Then estimate plasma cortisol, 24 hour urinary 17 KS and KGS including the period of infusion</li><li>Alternately cortrosyn (an ACTH analogue) can be given at a dose of 0.25 mg IM or IV in adults or 0.125 mg in children less than 2 years</li><li>Blood is collected at time 0, 30, and 60 minutes for cortisol and aldosterone</li><li>Addison''s disease (primary adrenal insufficiency) - neither cortisol nor aldosterone increases over baseline</li><li>Secondary adrenal insufficiency - caused by pituitary insufficiency or suppression by exogenous steroids - cortisol does not change but aldosterone increases</li></ul><h3>Elevated in:</h3><ul><li>Adrenal hyperplasia</li><li>Adrenal adenoma</li><li>Cushing''s syndrome</li></ul><h3>Negative in:</h3><ul><li>Adrenal carcinoma</li><li>Addison''s disease</li><li>Secondary adrenal insufficiency</li></ul><h3>Synonyms:</h3><ul><li>Adreno corticotropin stimulation test</li></ul>', 'A'),
(3, 'Adenosine deaminase estimation', '<h3>Indications:</h3><ul><li>To diagnose tuberculosis in tissue fluids</li></ul><h2>SAMPLE REQUIREMENT</h2><ul><li>Serum</li><li>CSF</li><li>Pleural</li><li>Ascitic fluid   2ml</li></ul><h2>METHOD</h2><h3>Elevated in:</h3><ul><li>Intra abdominal tuberculous effusions</li></ul><h3>Synonyms:</h3><ul><li>ADA estimation</li></ul>', 'A'),
(4, 'Aerobic culture, bacterial with sensitivity', '<h3>Indication:</h3><ul><li>To identify aeorobic bacteria</li><li>Bacterial infection</li></ul>', 'A'),
(5, 'AFB-antibody panel', '<h2>NOTES</h2><h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Anti TB antibody   supplementary, not confirmatory</li><li>TB may be confirmed if positive Anti TB antibody along with ESR and positive mantoux</li></ul>', 'A'),
(6, 'AFB-by flourescent microscopy', '<h2>SAMPLE REQUIREMENT</h2><ul><li>AFB detection in any tissue fluid</li></ul>', 'A'),
(7, 'AFB-Culture (LJ medium)', '<h2>NOTES</h2><ul><li>Usually takes 42 days</li><li>Clinical history required</li></ul><h2>SAMPLE REQUIREMENT</h2><ul><li>Sample in sterile container (except blood)</li></ul><h2>SPECIAL INSTRUCTION</h2><ul><li>Clinical history required</li></ul><h2>METHOD</h2><ul><li>Microbiology</li><li>Routine culture for TB</li></ul>', 'A'),
(8, 'AFB-Culture (Radiometric)', '<h2>NOTES</h2><ul><li>Usually takes  7 to 42 days</li><li>Clinical history required</li></ul><h2>SAMPLE REQUIREMENT</h2><ul><li>Sample in sterile container (except blood)</li></ul><h2>METHOD</h2><ul><li>BACTEC</li><li>More specific test for TB    in any tissue except blood</li></ul>', 'A'),
(9, 'Airway hyperresponsiveness test', '<h2>NORMAL VALUE</h2><ul><li>Asthma</li></ul><h3>Contraindications:</h3><ul><li>Cholinergic hypersensitivrty - cholinergic urticaria or angio-oedema or both</li><li>Allergy to histamine/methacholine</li><li>Unstable cardiac status - recent MI, arrhythmia or heart failure</li><li>Pregnancy</li><li>Severe baseline obstruction with FEV1 less than 1.5</li></ul><h3>Synonyms:</h3><ul><li>Histamine challenge test</li><li>Methacholine challenge test</li></ul>', 'A'),
(10, 'Albert’s Stain for C. diphtheria', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Diphtheria</li></ul>', 'A'),
(11, 'Alkaline phosphatase (ALP)', '<h2>NOTES</h2><ul><li>Rise less than 2 times in  hepatocellular disease</li><li>Rise more than  2 times in biliary tract obstruction</li></ul><h3>Hepatocellular damage:</h3><ul><li>SGPT - increased markedly</li><li>Alk.phosphatase - slight increase</li></ul><h3>Biliary obstruction:</h3><ul><li>Alk.phosphatase - increased markedly</li><li>SGPT - slight increase</li></ul><h3>Drugs causing increased values of alkaline phosphatase:</h3><ul><li>Albumin</li><li>Anabolics</li><li>Colchicine</li><li>Ergosterol</li><li>Isoniazid</li><li>PAS</li><li>Tolbutamide</li><li>Rifampicin</li><li>Sulphonamides</li><li>Methyl testosterone</li></ul><h2>NORMAL VALUE</h2><ul><li>Obstructive jaundice</li><li>Hepatocellular carcinoma</li><li>Biliary obstruction</li></ul>', 'A'),
(12, 'Ammonium chloride loading test', '<h3>Indications:</h3><ul><li>Unexplained hyperchloraemic metabolic acidosis</li><li>Renal tubular acidosis</li></ul>', 'A'),
(13, 'Amniocentesis', '<h2>NOTES</h2><ul><li>Chorionic villus sampling (CVS) has many  practical advantages over amniocentesis, including its earlier performance and quicker results</li><li>CVS is performed as a transcervical catheter procedure- majority of the time, therefore, there are no needles and the procedure is painless</li></ul><h3>Indications:</h3><ul><li>Maternal age more than 35</li><li>Congenital abnormalities in earlier babies</li><li>Risk of hereditary diseases</li></ul><h2>SPECIAL INSTRUCTION</h2><ul><li>Done between 14-16 weeks under ultrasonographic guidance</li></ul><h2>METHOD</h2><ul><li>Aspiration of amniotic fluid</li></ul>', 'A'),
(14, 'Basophilic stippling', '<ul><li>Lead or heavy metal poisoning</li><li>Thalassemia</li><li>Severe anemia</li></ul>', 'B'),
(31, 'Bentiromide test', '<h2>NOTES</h2><ul><li>Bentiromide - tripeptide (N-benzoyl-L-tyrosyl-PABA) when given orally, cleaved by chymotrypsin into benzoyl tyrosins and PABA, is absorbed and excerted in urine</li></ul><h2>SAMPLE REQUIREMENT</h2><h3>Decreased in:</h3><ul><li>Chronic pancreatitis</li></ul>', 'B'),
(32, 'Bicarbonate infusion test', '<ul><li>Proximal renal tubular acidosis</li></ul>', 'B'),
(33, 'Bile acid breath test', '<ul><li>Blind loop syndrome</li><li>Ileal disease or resection</li></ul>', 'B'),
(38, 'Blood culture', '<h3>Indications:</h3><ul><li>To identify the infective agent and determine its drug sensitivity</li></ul><h2>SPECIAL INSTRUCTION</h2><ul><li>Culture is repeated at intervals in order to confirm any change in characteristics of the infecting agent and to exclude superinfections</li></ul>', 'B'),
(39, 'Ascitic fluid, glucose', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Biochemical method</li><li>Diabetes mellitus</li><li>Cirrhosis</li><li>Nephrosis</li><li>Congestive heart failure</li><li>Tumours</li><li>Peritonitis due to TB or perforated viscus</li><li>Hypoalbuminemia</li></ul>', 'A'),
(36, 'Blood ammonia level', '<h2>NOTES</h2><ul><li>May suggest the cause of coma as hepatic failure</li></ul><h3>Indications:</h3><ul><li>Coma</li></ul><h2>SAMPLE REQUIREMENT</h2><h2>NORMAL VALUE</h2><h3>Elevated in:</h3><ul><li>Hepatic coma</li></ul>', 'B'),
(37, 'Blood culture and sensitivity (aerobic and anerobic)', '<h2>SAMPLE REQUIREMENT</h2><ul><li>Bacterial infections   culture and sensitivity tests</li></ul>', 'B'),
(34, 'Biuret test', '<h2>NOTES</h2><ul><li>A liver function test to assess total serum protein estimation in blood</li></ul><h3>Indication:</h3><ul><li>Liver function test</li></ul><h2>METHOD</h2><ul><li>Violet colour is developed when a drop of copper sulphate is added to a solution of protein in caustic soda</li></ul>', 'B'),
(35, 'Bleeding time', '<h2>NOTES</h2><ul><li>Bleeding time is prolonged when the level of platelets is decreased or when the platelets are qualitatively abnormal</li><li>Bleeding time can be either normal or prolonged in von Willebrand''s disease. It will definitely be prolonged if aspirin is administered prior to testing</li></ul><h2>NORMAL VALUE</h2><ul><li>Duke, Ivy - 1-6 minutes</li><li>Template - 3-10 minutes</li></ul><h2>METHOD</h2><ul><li>Duke''s method</li><li>Ivy''s method</li><li>Template method</li></ul><h3>Elevated in:</h3><ul><li>Thrombocytopenia</li><li>Platelet dysfunction syndromes - Bernard-Soulier disease, Glanzmann''s disease</li><li>Decrease or abnormality in plasma factors such like von Willebrand''s factor and fibrinogen</li><li>Vascular defects - especially in walls of the small blood vessels - capillary wall abnormalities</li><li>Severe liver disease - cirrhosis</li><li>Leukemia</li><li>Aplastic anaemia and other myeloproliferative disorders</li><li>DIC disease</li><li>Uremia</li><li>Ehlers-Danlos syndrome</li><li>Afibrinogenaemia</li><li>Platelet count less than 100,000/mm3</li><li>Acquired fibrinogen disorders</li><li>Acetyl salicylic acid - aspirin therapy</li><li>Dextran</li><li>Streptokinase</li><li>Alcohol derivatives</li><li>Warfarin</li><li>Antiinflammatory medications</li><li>Urokinase</li><li>P lactam antibiotics</li><li>Moxalactam</li></ul>', 'B'),
(67, 'Serum, phosphotidylcholine serine – IgG', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid serine   IgG</li></ul>', 'S'),
(68, 'Serum, phosphotidylcholine serine – IgM', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid serine   IgM</li></ul>', 'S'),
(63, 'Serum, phosphotidylcholine enositol – IgG', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid enositol   IgG</li></ul>', 'S'),
(64, 'Serum, phosphotidylcholine enositol – IgM', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid enositol   IgM</li></ul>', 'S'),
(65, 'Serum, phosphotidylcholine ethanolamin – IgG', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid ethanolamin   IgG</li></ul>', 'S'),
(66, 'Serum, phosphotidylcholine ethanolamin – IgM', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Infertility</li></ul><h3>Synonyms:</h3><ul><li>Serum, phospholipid ethanolamin   IgM</li></ul>', 'S'),
(62, 'Serum, phosphorus', '<h2>NOTES</h2><ul><li>Acute renal failure has less clinical impact than in chronic renal failure in abnormalities phosphorus</li></ul><h3>Indications:</h3><ul><li>Management of chronic renal failure</li><li>Acute renal failure</li></ul><h2>SAMPLE REQUIREMENT</h2><ul><li>Serum</li></ul><h2>NORMAL VALUE</h2><ul><li>Children - 4-7 mg/100ml</li><li>Adults - 3-4.5 mg/100ml</li></ul><h2>METHOD</h2><h3>Elevated in:</h3><ul><li>Chronic renal failure</li><li>Acute renal failure</li><li>Hypoparathyroidism - surgical, pseudohypoparathyroidism</li><li>Hypervitaminosis-D</li><li>Secondary hyperparathyroidism</li><li>Bone disease - healing fractures</li><li>Addison''s disease</li><li>Childhood</li><li>Hemolysis of specimen</li></ul><h3>Decreased in:</h3><ul><li>Hyperparathyroidism</li><li>Steatorrhoea</li><li>Antacid ingestion</li><li>Alcoholism</li><li>Diabetes</li><li>Hyperalimentation</li><li>Acidosis</li><li>Alkalosis</li><li>Gout</li><li>Salicylate poisoning</li><li>Administration of IV steroids</li><li>Administration of IV glucose</li><li>Administration of IV insulin</li><li>Hypokalemia</li><li>Hypomagnesemia</li><li>Diuretics</li><li>Phosphate-binding  antacids</li><li>Rickets</li><li>Osteomalacia</li></ul>', 'S'),
(61, 'Bentiromide test', '<h2>NOTES</h2><ul><li>Bentiromide - tripeptide (N-benzoyl-L-tyrosyl-PABA) when given orally, cleaved by chymotrypsin into benzoyl tyrosins and PABA, is absorbed and excerted in urine</li></ul><h2>SAMPLE REQUIREMENT</h2><h3>Decreased in:</h3><ul><li>Chronic pancreatitis</li></ul>', 'B'),
(59, 'Basophilic stippling', '<ul><li>Lead or heavy metal poisoning</li><li>Thalassemia</li><li>Severe anemia</li></ul>', 'B'),
(60, 'Benedict''s test, urine', '<h2>NOTES</h2><ul><li>Qualitative, semi-quantitative</li><li>The precipitate may not appear until cooling, if the glucose present is only 0.1% or less</li><li>Sensitivity -  50 mg% or more</li></ul><h3>Drugs causing false positive test by reducing with Benedicts solution:</h3><ul><li>Acetylsalicylic acid</li><li>Chloral hydrate</li><li>Streptomycin</li></ul><h3>Indications:</h3><ul><li>To find out presence of glucose in urine</li></ul><h2>METHOD</h2><ul><li>The cupric ion is reduced to cuprous oxide</li><li>To 5 ml of Benedict''s qualitative reagent add 8 drops of urine (0.5 ml)</li><li>Heat to boiling and set in a boiling water bath for 5 minutes or boil it over a flame for 2 minutes</li><li>Diabetes mellitus</li><li>Blue to cloudy green colour - negative</li><li>Yellow green - positive (less than  0.5% glucose)</li><li>Greenish yellow - positive, more intense than yellow green (0.5-1% glucose)</li><li>Yellow  -  positive, more intense than  greenish yellow (1-2% glucose)</li><li>Orange to brick red - positive, more intense than yellow (over 2% glucose)</li></ul>', 'B'),
(55, 'AFB-Culture (Radiometric)', '<h2>NOTES</h2><ul><li>Usually takes  7 to 42 days</li><li>Clinical history required</li></ul><h2>SAMPLE REQUIREMENT</h2><ul><li>Sample in sterile container (except blood)</li></ul><h2>METHOD</h2><ul><li>BACTEC</li><li>More specific test for TB    in any tissue except blood</li></ul>', 'A'),
(56, 'Airway hyperresponsiveness test', '<h2>NORMAL VALUE</h2><ul><li>Asthma</li></ul><h3>Contraindications:</h3><ul><li>Cholinergic hypersensitivrty - cholinergic urticaria or angio-oedema or both</li><li>Allergy to histamine/methacholine</li><li>Unstable cardiac status - recent MI, arrhythmia or heart failure</li><li>Pregnancy</li><li>Severe baseline obstruction with FEV1 less than 1.5</li></ul><h3>Synonyms:</h3><ul><li>Histamine challenge test</li><li>Methacholine challenge test</li></ul>', 'A'),
(57, 'Albert’s Stain for C. diphtheria', '<h2>SAMPLE REQUIREMENT</h2><h2>METHOD</h2><ul><li>Diphtheria</li></ul>', 'A'),
(58, 'Alkaline phosphatase (ALP)', '<h2>NOTES</h2><ul><li>Rise less than 2 times in  hepatocellular disease</li><li>Rise more than  2 times in biliary tract obstruction</li></ul><h3>Hepatocellular damage:</h3><ul><li>SGPT - increased markedly</li><li>Alk.phosphatase - slight increase</li></ul><h3>Biliary obstruction:</h3><ul><li>Alk.phosphatase - increased markedly</li><li>SGPT - slight increase</li></ul><h3>Drugs causing increased values of alkaline phosphatase:</h3><ul><li>Albumin</li><li>Anabolics</li><li>Colchicine</li><li>Ergosterol</li><li>Isoniazid</li><li>PAS</li><li>Tolbutamide</li><li>Rifampicin</li><li>Sulphonamides</li><li>Methyl testosterone</li></ul><h2>NORMAL VALUE</h2><ul><li>Obstructive jaundice</li><li>Hepatocellular carcinoma</li><li>Biliary obstruction</li></ul>', 'A');
